def PROFILE(message, solved_tasks, OPEN_CATALOG):
    len_solved_tasks = len(solved_tasks)
    len_OPEN_OPEN_CATALOG = len(OPEN_CATALOG())
    if len_solved_tasks <= len_OPEN_OPEN_CATALOG / 100 * 10:
        level = '–£—á–µ–Ω–∏–∫'
    elif len_solved_tasks <= len_OPEN_OPEN_CATALOG / 100 * 20:
        level = '–ó–Ω–∞—Ç–æ–∫'
    elif len_solved_tasks <= len_OPEN_OPEN_CATALOG / 100 * 30:
        level = '–ü—Ä–æ—Ñ–∏'
    elif len_solved_tasks <= len_OPEN_OPEN_CATALOG / 100 * 40:
        level = '–ú–∞—Å—Ç–µ—Ä'
    elif len_solved_tasks <= len_OPEN_OPEN_CATALOG / 100 * 60:
        level = '–ì—É—Ä—É'
    elif len_solved_tasks <= len_OPEN_OPEN_CATALOG / 100 * 70:
        level = '–ú—É–¥—Ä–µ—Ü'
    elif len_solved_tasks <= len_OPEN_OPEN_CATALOG / 100 * 80:
        level = '–ü—Ä–æ—Å–≤–µ—Ç–ª–µ–Ω–Ω—ã–π'
    elif len_solved_tasks <= len_OPEN_OPEN_CATALOG / 100 * 90:
        level = '–û—Ä–∞–∫—É–ª'
    elif len_solved_tasks == len_OPEN_OPEN_CATALOG:
        level = '–í—ã—Å—à–∏–π —Ä–∞–∑—É–º'
    return(
    f'–ü—Ä–æ—Ñ–∏–ª—å —É—á–µ–Ω–∏–∫–∞: {message.from_user.first_name}:\n\n'
    f'–£—Ä–æ–≤–µ–Ω—å –∑–Ω–∞–Ω–∏–π: {level}\n'
    f'–†–µ—à–µ–Ω–æ –∑–∞–¥–∞—á: {len(solved_tasks)}\n'
    f'–ù–æ–º–µ—Ä–∞ —Ä–µ—à–µ–Ω–Ω—ã—Ö –∑–∞–¥–∞—á: {str(solved_tasks)[1:-1]}\n'
    )